import React from 'react';
import Accordion from 'react-bootstrap/Accordion'
import {Card } from 'react-bootstrap';
import '../assets/css/FormaPago.css';
import axios from 'axios'
import Modalbutton from '../Components/Modal';
import '../assets/css/Transferencia.css';


class Transferencia extends React.Component{

  constructor(){
    super()
    this.state = {
        items: {},
        person_type:'',
        document_type:'',
        document_number:'',
        bank_id:'',
        boton:'false',
        document_numberError:'',
        names:'',
        email:'',
        phone:'',
        product_id:'',
        user_id:''

    }
}

valid(){

  if ( !this.state.document_number )
  {
    this.setState(
      {document_numberError:'invalid'}
      )
  }
  else{
    return true 
  }

}


  componentDidMount(){
  axios.get(`https://kieroapi.net/pse_banks`) /*Here must be the correct endpoint */
  .then(res => {
      this.setState({items:res.data});
  })
  .catch(err => {
      console.log(err)
  })
  }

submitHandler = e => {
        e.preventDefault();

      axios.post('https://kieroapi.net/pse_payment', {
          names:"maria",
          email:"maria@gmail.com",
          phone:'30255684',
          product_id:'2374824',
          user_id:"1093",
          person_type: "N",
          document_type:"CC",
          document_number:this.state.document_number,
          bank_pseCode:"1040"
      })
      .then(res => {
        console.log(res.data)
      }).catch(e => {
        console.log(e)
      })

      this.setState(
        {document_numberError:""}
        )



      if(this.valid()){

        this.setState({
          boton:'active'
        })  

      }


    };

changeHandler = e => {
  this.setState({ [e.target.name]: e.target.value });
};

    render(){
      const{person_type,document_type,document_number,bank_pseCode,name,email,phone}=this.state;

        return(

          <div>
            <Card>

            <Accordion.Toggle as={Card.Header} variant="link" eventKey="2" className='tamano '>
               Transferencia desde PSE.
            </Accordion.Toggle>

            <Accordion.Collapse eventKey="2" className='tam'>
            <Card.Body>

<form
      className="needs-validation"
      onSubmit={this.submitHandler}
      >
            <div className="form-group col-12">

                                  <div className="input-group col-12">
                                     <input className="form-control" name='name' type='text' value={name}  placeholder="Nombre Completo*" required onChange={this.changeHandler}/>
                                     <div className="input-group-append">                                              
                                  </div>
                              </div>

                              <div className="input-group col-12 mt-3">
                                     <input className="form-control" name='email' type='email' value={email}  placeholder="Email*" required onChange={this.changeHandler}/>  
                                     <div className="input-group-append">                                              
                                  </div>
                              </div>      

                              <div className="input-group col-12 mt-3">
                                     <input className="form-control" name='email' type='number' value={phone}  placeholder="Telefono*" required onChange={this.changeHandler}/>
                                     <div className="input-group-append">                                              
                                  </div>
                              </div>      



                                  <label htmlFor="inputState " className='tes mt-3'>BANCOS</label>
                                  <select id="inputState" className="form-control sel" name='bank_id' value={bank_pseCode} onChange={this.changeHandler} required >


                                { /* change the params id for the description*/}
                                  {Object.keys(this.state.items).length > 0 && this.state.items.banks.map(bank => <option key={bank.pseCode} value={bank.id}>{bank.description}</option>  )}


                                  </select>
                                </div>

                               <div className="form-group col-12">
                                  <label htmlFor="inputState " className='tes' >TIPO DE PERSONA</label>
                                  <select id="inputState" className="form-control sel" name='person_type' value={person_type} onChange={this.changeHandler} required>
                                  <option defaultValue>N</option>
                                    <option>J</option>
                                  </select>
                                </div>

                                 <div className="form-group col-12">
                                  <label htmlFor="inputState " className='tes' >DOCUMENTO DE IDENTIFICACION</label>
                                  <select id="inputState" className="form-control sel" name='document_type' value={document_type} onChange={this.changeHandler} required>
                                  <option defaultValue>CC</option>
                                    <option>CE</option>


                                  </select>
                                </div>





                                <div className="form-group col-12">
                                <input className="form-control col-12" type="text" name="document_number" minLength='5' maxLength='40' value={document_number} placeholder="Numero documento*"  onChange={this.changeHandler} required/>
                                <p style={{color:'red',fontSize:'14px'}}> {this.state.document_numberError}</p>
                                </div>
                                <button type="submit" className="btn btn-outline-danger btn-block mt-3">Continuar Compra</button>




                </form>
                <div className={this.state.boton}> <Modalbutton/></div>

            </Card.Body>
            </Accordion.Collapse>
        </Card>

        </div>
        )
    }
}

export default Transferencia;
