import React from 'react';
import Modal from 'react-responsive-modal';
import '../../assets/css/modal.css'
import axios from 'axios'


class Direcciones extends React.Component{

  constructor(props){
    super(props);
    this.state={
        data:{}
    }
  }

  submitHandler = e => {
  e.preventDefault();
  axios.post('https://kieroapi.net/pse_payment', this.state)
  .then((response) => {
    console.log(response);
  }, (error) => {
    console.log(error);
  });

};

changeHandler = e => {
this.setState({ [e.target.name]: e.target.value });
};


onOpenModal = () => {
    this.setState({ open: false });
};

onCloseModal = () => {
    this.setState({ open: false });
};




render(){
    const { open } = this.state;
    const { nombre, apellidos, telefono, ciudad, barrio, direccion } = this.state;

    return(
      <div>
        <Modal open={open}  showCloseIcon = {false}  closeOnOverlayClick={false} center>


        <div className='uno col-12'>
          <form className="was-validate" onSubmit={this.submitHandler}>
            <p>Ingresa Direccion de envio</p>
            <input className="form-control " name='nombre'  type="text" value={nombre}placeholder="Nombre*" required onChange={this.changeHandler}/>
            <input className="form-control mt-3" name='apellidos' type="text" value={apellidos}placeholder="Apellidos*" required onChange={this.changeHandler}/>
            <input className="form-control mt-3" name='telefono' value={telefono} type="text" placeholder="Telefono*" required onChange={this.changeHandler}/>
            <input className="form-control mt-3" name='ciudad' value={ciudad} type="text" placeholder="Ciudad*" required onChange={this.changeHandler}/>
            <input className="form-control mt-3" name='barrio' value={barrio} type="text" placeholder="Barrio*" required onChange={this.changeHandler}/>
            <input className="form-control mt-3" name='direccion' value={direccion} type="text" placeholder="Direccion*" required onChange={this.changeHandler}/>


            <div className={this.state.botone}>
            <button type="submit" onClose={false} className="btn btn-outline-danger btn-block mt-3">Continuar</button>
            </div>
          </form>
        </div>
        </Modal>
      </div>
    )
}

}

export default Direcciones;
